import React, { useEffect, useState } from "react"
import { useParams, Link } from "react-router-dom"
import { readDeck, deleteDeck } from "../utils/api/index.js"

function Deck () {
    const params = useParams();

    const [deck, setDeck] = useState([]);

    useEffect(() => {
        const getAPIData = async () => {
            const getData = await readDeck(params.id);
            setDeck(getData);
        }
        getAPIData();
    }, [])

    function deleteHandler() {
        deleteDeck(deck.id)
        window.location.reload();
    }

    if (deck.length === 0) return <p>Loading...</p>
        else {
            return (
                <>
                    <nav aria-label="breadcrumb">
                        <ol className="breadcrumb">
                            <li className="breadcrumb-item"><Link to="/">Home</Link></li>
                            <li className="breadcrumb-item active" aria-current="page">{deck.name}</li>
                        </ol>
                    </nav>

                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">{deck.name}</h5>
                            <p className="card-text">{deck.description}</p>
                            <div className="d-flex">
                            <a href="/decks" className="btn btn-secondary">View</a>
                            <a href="sheesh" className="btn btn-primary">Study</a>
                            <div className="ml-auto">
                                <button onClick={() => {deleteHandler()}} className="btn btn-primary bg-danger">Delete</button>
                            </div>
                            </div>
                        </div>
                    </div>
                </>
                
            )
        }
    
}

export default Deck
