import React, { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import { readDeck } from "../utils/api/index.js"

function Study () {

    const { id } = useParams();

    const [deck, setDeck] = useState([]);

    const [currentCard, setCurrentCard] = useState(0);
    
    const [currentMessage, setCurrentMessage] = useState("No message");

    useEffect(() => {
        const getAPIData = async () => {
            const getData = await readDeck(id);
            setDeck(getData);
        }
        getAPIData();
    }, [])

    if (deck.length === 0) return <p>loading...</p>;

    const { cards } = deck;
    let card = cards[currentCard];
    setCurrentMessage(card.front);
    let flipped = false;

    function flipCard () {
        setCurrentMessage(flipped ? card.back : card.front)
        
        console.log(flipped ? card.back : card.front);
        flipped = !flipped;
    }

    function nextCard () {
        setCurrentCard(currentCard + 1)
    }

    return (
        <>

            <nav aria-label="breadcrumb">
                <ol className="breadcrumb">
                    <li className="breadcrumb-item"><a href="/">Home</a></li>
                    <li className="breadcrumb-item"><a href={`/decks/${id}`}>Library</a></li>
                    <li className="breadcrumb-item active" aria-current="page">Study</li>
                </ol>
            </nav>
            <h1>Study: {deck.name}</h1>

        <div className="card">
            <div className="card-body">
                <h5 className="card-title">Card {currentCard + 1} of {cards.length}</h5>
                <p className="card-text">{currentMessage}</p>
                <div className="d-flex">
                    <button onClick={() => flipCard()} className="btn btn-secondary">Flip</button>
                    <button onClick={() => nextCard()} className="btn btn-primary">Next</button>
                </div>
            </div>
        </div>
        </>
    )
}

export default Study
