import React from "react"
import { useHistory } from "react-router-dom"
import DeckView from "./DeckView.js"
import "./Main.css"

function Main ({ decks, deckDeleteHandler }) {

    const history = useHistory();

    const mappedList = decks.map((deck) => <DeckView deck={deck} deleteHandler={deckDeleteHandler} />)

    return (
        <>
            <button onClick={() => history.push("/decks/new")} type="button" className="btn btn-secondary btn-lg">+ Create Deck</button>
            {mappedList}
            
        </>
    )
}

export default Main
