import React, { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import { readDeck } from "../utils/api/index.js"

function Study () {

    const { id } = useParams();

    const [deck, setDeck] = useState([]);

    const [currentCard, setCurrentCard] = useState(0);

    const { cards } = deck;
    let card = cards[currentCard];

    function nextCard () {
        setCurrentCard(currentCard + 1)
    }

    useEffect(() => {
        const getAPIData = async () => {
            const getData = await readDeck(id);
            setDeck(getData);
        }
        getAPIData();
    }, [])

    if (deck.length === 0) return <p>loading...</p>
    return (
        <>

            <nav aria-label="breadcrumb">
                <ol className="breadcrumb">
                    <li className="breadcrumb-item"><a href="/">Home</a></li>
                    <li className="breadcrumb-item"><a href={`/decks/${id}`}>Library</a></li>
                    <li className="breadcrumb-item active" aria-current="page">Study</li>
                </ol>
            </nav>
            <h1>Study: {deck.name}</h1>

            <div className="card">
            <div className="card-body">
                <h5 className="card-title">{deck.name}</h5>
                <p className="text-right">{deck.cards.length} cards</p>
                <p className="card-text">{deck.description}</p>
                <div className="d-flex">
                <a href={`/decks/${deck.id}`} className="btn btn-secondary">View</a>
                <a href={`/decks/${deck.id}/study`} className="btn btn-primary">Study</a>
                <div className="ml-auto">
                    <button onClick={() => {deleteHandler()}} className="btn btn-primary bg-danger">Delete</button>
                </div>
                </div>
            </div>
        </div>
        </>
    )
}

export default Study
