import React, { useState, useEffect } from "react"
import { Link, useHistory, useParams } from "react-router-dom"
import { readDeck, updateDeck } from "../utils/api/index.js"

function EditDeck() {

    const { id } = useParams;

    const history = useHistory();

    const [deck, setDeck] = useState()

    useEffect(() => {
        const getAPIData = async () => {
            const getData = await readDeck(id);
            setDeck(getData);
        }
        getAPIData();
    }, [])

    const initialFormData = {
        name: deck.name,
        description: deck.description
    }
    const [formData, setFormData] = useState(initialFormData);

    const changeHandler = ({ target }) => {
        setFormData({
            ...formData,
            [target.name]: target.value
        })
    }
    
    const submitHandler = async (event) => {
        event.preventDefault()
        const data = await updateDeck(formData)
        setFormData(initialFormData);
        history.push("/")
    }

    return (
        <>
            <nav aria-label="breadcrumb">
            <ol className="breadcrumb">
                <li className="breadcrumb-item"><Link to="/">Home</Link></li>
                <li className="breadcrumb-item"><Link to="/">Sheesh</Link></li>
                <li className="breadcrumb-item active" aria-current="page">Create Deck</li>
            </ol>
            </nav>
            <h3>Create Deck</h3>
            <form onSubmit={submitHandler}>
                <p>Name</p>
                <input
                    type="text"
                    name="name"
                    id="name"
                    placeholder="Deck Name"
                    required
                    onChange={changeHandler}
                />
                <hr/>
                <p>Description</p>
                <textarea
                    type="text"
                    name="description"
                    id="description"
                    placeholder="Add a Deck Description"
                    required
                    onChange={changeHandler}
                />
                <hr/>
                <button type="button" className="btn btn-secondary" onClick={() => history.push("/")}>Cancel</button>
                <button type="submit" className="btn btn-primary">Submit</button>
                <button type="reset" className="btn btn-danger" onClick={() => setFormData(initialFormData)}>Reset Form</button>
            </form>
        </>
        
    )
}

export default EditDeck;
